import React, { useState } from "react";
import Cell from "./Cell";
import "./Board.css";

function Board({ nrows, ncols, chanceLightStartsOn }) {
  const [board, setBoard] = useState(createBoard());
  const [gameWon, setGameWon] = useState(false);

  /** create a board nrows high/ncols wide, each cell randomly lit or unlit */
  function createBoard() {
    let initialBoard = [];

    for (let i = 0; i < nrows; i++) {
      let row = [];
      for (let j = 0; j < ncols; j++) {
        const isLit = Math.random() < chanceLightStartsOn;
        row.push(isLit);
      }
      initialBoard.push(row);
    }

    return initialBoard;
  }

  function hasWon() {
    // Check if all cells are unlit
    return board.every(row => row.every(cell => !cell));
  }

  function flipCellsAround(coord) {
    if (gameWon) return; // Don't allow further moves if game is already won

    setBoard(oldBoard => {
      const [y, x] = coord.split("-").map(Number);
      const boardCopy = [...oldBoard];

      const flipCell = (y, x) => {
        boardCopy[y][x] = !boardCopy[y][x];
        const directions = [[0, 1], [0, -1], [1, 0], [-1, 0]];
        directions.forEach(([dy, dx]) => {
          const ny = y + dy;
          const nx = x + dx;
          if (nx >= 0 && nx < ncols && ny >= 0 && ny < nrows) {
            boardCopy[ny][nx] = !boardCopy[ny][nx];
          }
        });
      };

      flipCell(y, x);

      // Check for winning condition after updating board
      if (hasWon()) {
        setGameWon(true);
      }

      return boardCopy;
    });
  }

  // Create table board
  const tableBoard = [];
  for (let i = 0; i < nrows; i++) {
    let row = [];
    for (let j = 0; j < ncols; j++) {
      const coord = `${i}-${j}`;
      row.push(
        <Cell
          key={coord}
          isLit={board[i][j]}
          flipCellsAroundMe={() => flipCellsAround(coord)}
        />
      );
    }
    tableBoard.push(<tr key={i}>{row}</tr>);
  }

  return (
    <div className="Board">
      {gameWon ? (
        <div className="Board-winner">You Won!</div>
      ) : (
        <table className="Board-table">
          <tbody>{tableBoard}</tbody>
        </table>
      )}
    </div>
  );
}

export default Board;
